import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import Ingreso from './components/Ingreso/Ingreso';
import Registro from './components/Ingreso/Registro';
import Lugares from './components/Lugares/Lugares';
import Diversion from './components/Diversion/Diversion';
import Restaurantes from './components/Restaurantes/Restaurantes';
import Nosotros from './components/Nosotros/Nosotros';
import logo from './components/imagenes/logo.PNG'; // Asegúrate de que esta ruta sea correcta

function App() {
    const [showImage, setShowImage] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const timer = setTimeout(() => {
            setShowImage(false);
            navigate('/Ingreso'); // Redirige a la página de inicio de sesión
        }, 5000); // 5000 ms = 5 segundos

        return () => clearTimeout(timer); // Limpia el timeout si el componente se desmonta
    }, [navigate]);

    return (
        <Router>
            <div>
                <Routes>
                    <Route path='/Ingreso' element={<Ingreso />} />
                    <Route path='/Registro' element={<Registro />} />
                    <Route path='/Lugares' element={<Lugares />} />
                    <Route path='/Restaurantes' element={<Restaurantes />} />
                    <Route path='/Diversion' element={<Diversion />} />
                    <Route path='/Nosotros' element={<Nosotros />} />
                    <Route path='/' element={showImage ? (
                        <div className="image-container">
                            <img src={logo} alt="Logo de la aplicación" />
                        </div>
                    ) : null} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
